mapper.select:
select * 
from tms_projects p


       p.name like CONCAT ("%",#{project.name},"%")


      and p.valid=#{project.valid}


ORDER BY p.createdTime DESC

limit #{pageObject.startIndex},#{pageObject.pageSize};

select count(*) from tms_projects p


      p.name like CONCAT ("%",#{name},"%")


      and p.valid=#{valid}

mapper.update:
update tms_projects
set valid=#{valid}
where id in 

    #{item}
